import classes from "./AddUser.module.css";
import Button from "../../UI/Card/Button/Button.js";
import { useState } from "react";
import Card from "../../UI/Card/Card";
const AddUser = (props) => {
  const submitHandler = (event) => {
    event.preventDefault();
    const Data = {
    UserName: username,
    Age: age,
  };
  props.onSendData(Data);
  };
  const [username, setUsername] = useState("");
  const usernameHandler = (event) => {
    setUsername(event.target.value);
  };

  const [age, setAge] = useState(0);
  const ageHandler = (event) => {
    setAge(event.target.value);
  };
  return (
    <Card className={classes.input}>
    <form  onSubmit={submitHandler}>
      <label htmlFor="username">Username</label>
      <input id="username" onChange={usernameHandler} type="text" />
      <label htmlFor="age">Age (Years)</label>
      <input id="age" onChange={ageHandler} type="number" />
      <Button type="submit" onClick={submitHandler}/>Add Us
    </form>
    </Card>
  );
};
export default AddUser;
