import { useState } from "react";
import AddUser from "../../Users/AddUser/AddUser";
import UserList from "../../UserList/UserList";
import ErrorModal from "../../ErrorModal/ErrorModal";

const Card = (props) => {
  const [userInput, setUserInput]= useState("");
  const DataHandler=(event)=>{
    setUserInput(event);
  }
  return (
    <div>
      <div className="">{props.children}</div>
      <AddUser onSendData={DataHandler}/>
      {userInput.Age<0 && <ErrorModal/>}
      {userInput.UserName==="" && <ErrorModal />}
      {!userInput &&  <h1 style={{textAlign:"center"}}> There is no Data</h1>}
      {userInput &&  <UserList userInputs={userInput}></UserList>}
    </div>
  );
};
export default Card;
